import React from 'react'

const Form = ({ school }) => {

    const [schools, setSchools] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:8000/school/:school', {
            'method': 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then(resp => resp.json())
            .then(resp => setSchools(resp))
            .catch(error => console.log(error))
    }, [])
  

    return (
        <>
            <div className="text-center h3">Edit the information :</div>

            {school ?
                (
                    <>
                    <div className='container'>
                    <label className="form-label" htmlFor="name">Name :</label>
                    <input class="form-control" type="text" id='name'/>
                    <label className="form-label" htmlFor="img">Image :</label>
                    <input class="form-control" type="text" id='img'/>
                    <label className="form-label" htmlFor="disc">Discription :</label>
                    <input class="form-control" type="text" id='disc'/>
                    <label className="form-label" htmlFor="contact">Contact Info :</label>
                    <input class="form-control" type="text" id='contact'/>
                    </div>
                    </>
                ) : null
            }

            {school}
        </>
    )
}

export default Form
