import React from 'react';
import {useState,useEffect} from 'react';

const School_discription = ({id}) => {

  const [schools, setSchools] = useState([])
  
  useEffect((id) => {


    fetch('http://localhost:8000/school/1', {
        'method': 'GET',
        headers: {
            'Content-Type': 'application/json',
        }
    })
        .then(resp => resp.json())
        .then(resp => setSchools(resp))
        .catch(error => console.log(error))
}, [])

  return (
    <>
    
    <div>School_discription</div>
    {schools.map(school => {
                    return (
                        <>
                            <div className="schools d-flex flex-row">
                                <div>
                                    <h2>{school.name}</h2>
                                    <img src={school.img_school} alt="" height={200} width={200} />
                                    <p>
                                        About School :
                                        {school.discription}
                                    </p>
                                    a
                                    
                                </div>
                            </div>
                           Id name : {id}
                        </>
                    )
                })}
    </>
    
  )
}

export default School_discription
