import React,{useState,useEffect} from 'react'
import API from '../API'
const Form = (props) => {

    const [name,setName]=useState(props.school.name)
    const [discription,setDisc]=useState(props.school.discription)
    const [img_school,setImg]=useState(props.school.img_school)
    const [conatact_no,setContact]=useState(props.school.conatact_no)
 

   const update_school=()=>{
    API.updateschool(props.school.id,{name,discription,img_school})
   }

    return (
        <>
            {props.school ? (
                <div className='container'>
                <label className="form-label" htmlFor="name">Name :</label>
                <input className="form-control" type="text" id='name' value={name} onChange={(e)=>setName(e.target.value)}/>
                <label className="form-label" htmlFor="disc">Image :</label>
                <input className="form-control" type="text" id='disc' value={discription} onChange={(e)=>setDisc(e.target.value)}/>
                <label className="form-label" htmlFor="img">Discription :</label>
                <input className="form-control" type="text" id='img' value={img_school} onChange={(e)=>setImg(e.target.value)}/>
                <label className="form-label" htmlFor="contact">Contact Info :</label>
                <input className="form-control" type="text" id='contact' value={conatact_no} onChange={(e)=>setContact(e.target.value)}/>
                <button className='btn btn-success' onClick={update_school}>Update</button>
                </div>
            ) : <div className='container'>
            <label className="form-label" htmlFor="name">Name :</label>
            <input className="form-control" type="text" id='name' onChange={(e)=>setName(e.target.value)}/>
            <label className="form-label" htmlFor="disc">Image :</label>
            <input className="form-control" type="text" id='disc' onChange={(e)=>setDisc(e.target.value)}/>
            <label className="form-label" htmlFor="img">Discription :</label>
            <input className="form-control" type="text" id='img' onChange={(e)=>setImg(e.target.value)}/>
            <label className="form-label" htmlFor="contact">Contact Info :</label>
            <input className="form-control" type="text" id='contact' onChange={(e)=>setContact(e.target.value)}/>
            <button className='btn btn-success' onClick={update_school}>Add School</button>
            </div>}
        </>
    )
}

export default Form
