import React,{useState,useEffect} from 'react'

const Form = (props) => {

    const [name,setName]=useState(props.school.name)
    const [disc,setDisc]=useState(props.school.discription)
    const [img,setImg]=useState(props.school.img_school)
    const [contact,setContact]=useState(props.school.conatact_no)
 

    const update_school=()=>{
        console.log(type(name))
        let item={name,disc,img,contact};
        fetch(`http://127.0.0.1:8000/school/${props.school.id}`,{
            'method':'PUT',
            headers:{
                'Accept': 'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify(item)
        })
        .then(response => {
            console.log(response)        
            if (response.ok) {
                return response.json();
              } else {
                 throw new Error('Something went wrong ...');
              }
         })
    }

    return (
        <>
            {props.school ? (
                <div className='container'>
                <label className="form-label" htmlFor="name">Name :</label>
                <input className="form-control" type="text" id='name' value={name} onChange={(e)=>setName(e.target.value)}/>
                <label className="form-label" htmlFor="disc">Image :</label>
                <input className="form-control" type="text" id='disc' value={disc} onChange={(e)=>setDisc(e.target.value)}/>
                <label className="form-label" htmlFor="img">Discription :</label>
                <input className="form-control" type="text" id='img' value={img} onChange={(e)=>setImg(e.target.value)}/>
                <label className="form-label" htmlFor="contact">Contact Info :</label>
                <input className="form-control" type="text" id='contact' value={contact} onChange={(e)=>setContact(e.target.value)}/>
                <button className='btn btn-success' onClick={update_school}>Update</button>
                </div>
            ) : null}
        </>
    )
}

export default Form
