import './App.css';
import School from './components/School';
import School_discription from './components/School_discription'
import Form from './components/form';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import {useState,useEffect} from 'react'
function App(props) {

  const [schools, setSchools] = useState([])
  const [editschool,setEditschool]=useState(null);
  useEffect(() => {
      fetch('http://127.0.0.1:8000/school/', {
          'method': 'GET',
          headers: {
              'Content-Type': 'application/json',
          },
      })
          .then(resp => resp.json())
          .then(resp => setSchools(resp))
          .catch(error => console.log(error))
  }, [])



  const edit_school=(id_name)=>{
    setEditschool(id_name)
  }

  return (
    <div className="App bg-light">
      <Router>
        <Routes>
          <Route path='/' element={<School schools={schools} edit_school={edit_school} /> }/>
          {editschool}
          <Route path='form:editschool.id' element={<Form school={editschool}/>}/>
        </Routes>
      </Router>
    </div>
  );
}

export default App;
