import React from 'react'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import School_discription from './School_discription'

const School = (props) => {

    // const [id,setId]=useState(null)

    const edit_school = (school) => {
        props.edit_school(school);
        console.log(school);
    }
    return (
        <div className="App bg-light">

            <h2 className='text-center'>Search Best school in Nepal :</h2>
            <div className="school_list mt-4">

                {props.schools.map(school => {
                    return (
                        <>
                            <div className="schools">
                                <div className="d-flex just-content-scenter">
                                    <h2>{school.name}</h2>
                                    <img src={school.img_school} alt="" height={200} width={200} />
                                    <p>
                                        About School :
                                        {school.discription}
                                    </p>
                                    <button className="btn btn-primary" onClick={() => edit_school(school)}>Change</button>
                                </div>
                            </div>
                            {/* Id name : {id} */}
                        </>
                    )
                })}
            </div>
        </div>
    );
}


export default School
