import React from 'react'
import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom';
import School_discription from './School_discription'

const School = ({schools,edit_school}) => {
 
    // const [id,setId]=useState(null)

    const edit=(school)=>{
        edit_school(school);
        console.log(school);
    }
    return (
        <div className="App bg-light">

            <h2 className='text-center'>Search Best school in Nepal :</h2>
            <div className="school_list mt-4">

                {school}
                {schools.map(school => {
                    return (
                        <>
                            <div className="schools d-flex flex-row">
                                <div>
                                    <h2>{school.name}</h2>
                                    <img src={school.img_school} alt="" height={200} width={200} />
                                    <p>
                                        About School :
                                        {school.discription}
                                    </p>
                                    <button class="btn btn-primary" onClick={()=>edit(school)}>Change</button>   
                                </div>
                            </div>
                           {/* Id name : {id} */}
                        </>
                    )
                })}
            </div>
        </div>
    );
}


export default School
