import { useState,useEffect } from 'react';
import './App.css';


function App() {

  const [schools,setSchools]=useState([])

  useEffect(() =>{
    fetch('http://127.0.0.1:8000/school/',{
      'method': 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    })
    .then(resp=>resp.json())
    .then(resp=>setSchools(resp))
    .catch(error => console.log(error))
  },[])
  return (
    <div className="App bg-light">

      <h2 className='text-center'>Search Best school in Nepal :</h2>
    <div className="school_list mt-4">
     {schools.map(school=>{
      return (
        <>
        <div className="schools d-flex flex-row">
          <div>
        <h2>{school.name}</h2>
        <img src={school.img_school} alt="" height={200} width={200} />
        <p>
          About School :
        {school.discription}
        </p>
        <button className='btn btn-primary '>About {school.name}</button>
          </div>
        </div>
        </>
      )
     })}
    </div>
    </div>
  );
}

export default App;
