import React from 'react';
import { useState, useEffect } from 'react';
import { useParams} from 'react-router-dom';
const School_discription = () => {

    const [school, setSchool] = useState([])
    
    useEffect(() => {


        const {id}=useParams
        fetch(`http://127.0.0.1:8000/school/${match.params.id}`, {
            'method': 'GET',
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(resp => resp.json())
            .then(resp => setSchool(resp))
            .catch(error => console.log(error))
    }, [])

    return (
        <>

            <div>School_discription</div>

            <div className="schools d-flex flex-row">
                <div>
                    <h2>{school.name}</h2>
                    <img src={school.img_school_link} alt="" height={200} width={200} />
                    <p>
                        About School :
                        {school.discription}
                    </p>
                    a

                </div>
            </div>



        </>

    )
}

export default School_discription
