import React from 'react'
import {useState,useEffect} from 'react'
import {Link} from 'react-router-dom'
const School = (props) => {
    const [schools, setSchools] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:8000/school/', {
            'method': 'GET',
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then(resp => resp.json())
            .then(resp => setSchools(resp))
            .catch(error => console.log(error))
    }, [])

    const [id,setId]=useState(null)

    const id_collecter=(id_name)=>{
        setId(id_name);
    }
    return (
        <div className="App bg-light">

            <h2 className='text-center'>Search Best school in Nepal :</h2>
            <div className="school_list mt-4">
                {schools.map(school => {
                    return (
                        <>
                            <div className="schools d-flex flex-row">
                                <div>
                                    <h2>{school.name}</h2>
                                    <img src={school.img_school} alt="" height={200} width={200} />
                                    <p>
                                        About School :
                                        {school.discription}
                                    </p>
                                    <button onClick={()=>id_collecter(school.id)}>Hey</button>
                                    <Link to='detail'>Discription</Link>
                                </div>
                            </div>
                            {id}
                        </>
                    )
                })}
            </div>
        </div>
    );
}


export default School
